function removeHtmlTag(strx,chop){
	if(strx.indexOf("<")!=-1){
		var s = strx.split("<");
		for(var i=0;i<s.length;i++){
			if(s[i].indexOf(">")!=-1){
				s[i] = s[i].substring(s[i].indexOf(">")+1,s[i].length);}}
		strx = s.join("");}

chop = (chop < strx.length-1) ? chop : strx.length-2;
while(strx.charAt(chop-1)!=' ' && strx.indexOf(' ',chop)!=-1) chop++;
strx = strx.substring(0,chop-1);
return strx+'...';
}

function createSummaryAndThumb(pID, pURL, pTITLE, pDATE, pAUTHOR){
	var div = document.getElementById(pID);
	var imgtag = "";
	var img = div.getElementsByTagName("img");
	var summ = posts_no_thumb_sum;
	if(img.length>=1) {
		imgtag = '<div class="img-post"><a href="'+ pURL +'" title="'+ pTITLE+'"><img width="1012" height="457" class="img-headline" src="'+img[0].src+' "/></a></div>';
		summ = posts_thumb_sum;
		}
    var summary = imgtag + '<div class="post-text"><h3 class="title-post"><a href="'+ pURL +'">' + pTITLE+ '</a></h3><p class="text-post d-sm-none d-md-block dni-none">' + removeHtmlTag(div.innerHTML,summ) + '</p></div>';
	div.innerHTML = summary;
}

function createSummaryAndThumb3(pID, pURL, pTITLE, pDATE, pAUTHOR){
	var div = document.getElementById(pID);
