  linkdata.innerHTML = taglink;
}

var slideIndex = 0;
var stop;
function showSlides() {
  var i;
  var slides = document.getElementsByClassName("slide-item");
  var dots = document.getElementsByClassName("dot");
  for (i = 0; i < slides.length; i++) {
    slides[i].classList.remove("inFade");//inFade
    slides[i].classList.add ("outfade");//outfade
    slides[i].style.opacity = 0 ;
    //slides[i].style.display = "none";
  }
  slideIndex++;
  if (slideIndex > slides.length) {slideIndex = 1}
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  //slides[slideIndex-1].style.display = "block";
  slides[slideIndex-1].style.opacity = 1 ;
  slides[slideIndex-1].classList.remove("outfade");//outfade
  slides[slideIndex-1].classList.add("inFade");//inFade
  dots[slideIndex-1].className += " active";

  stop = setTimeout(showSlides,8000); // Change image every 2 seconds
}

